package com.seleniumtraining;

public class LoopingDo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 2;
		int counter = 1; 
		
		do {
			
			System.out.println(num*counter);
			counter++;
			
			} while (counter <= 10 );
			
		}
			
	}


